TABLEATTRIBUTE.VALUE:
select A.TXN_ID, nvl( ( select TXN_DTETM from $$CTR_SCH.bskt_sls_summ where txn_id=A.txn_id and txn_dtetm between to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 00:00:00','dd/mm/yyyy hh24:mi:ss') and to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 23:59:59','dd/mm/yyyy hh24:mi:ss')),A.txn_dtetm ) TXN_DTETM, A.DIV_NO, A.STORE_NO, A.TXN_OFR_CD, A.CUST_RLTNSHP_NO, A.BUS_PROG_ID, A.TOT_RWD_PNT, A.TOT_RWD_AMT, A.BNS_PNT, A.BASE_PNT, 0 MAN_CR_PNT, A.TOT_TXN_AMT, A.PROC_FL, A.BATCH_RUN_ID, 'A' IND, 'SALES' TXN_TYPE_ID, B.CRE_DTETM POINT_ALCTN_DTTM, ( select CHKOUT_NO from $$CTR_SCH.bskt_sls_summ where txn_id=A.txn_id and txn_dtetm between to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 00:00:00','dd/mm/yyyy hh24:mi:ss') and to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 23:59:59','dd/mm/yyyy hh24:mi:ss')
) CHKOUT_NO, ( select TKT_NBR from $$CTR_SCH.bskt_sls_summ where txn_id=A.txn_id and txn_dtetm between to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 00:00:00','dd/mm/yyyy hh24:mi:ss') and to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 23:59:59','dd/mm/yyyy hh24:mi:ss')
) TKT_NBR from $$CTR_SCH.BSKT_SLS_RWD_SUMM A , $$CTR_SCH.BATCH_RUN_MASTER B where A.batch_run_id =B.batch_run_id and B.ODS_DLY_PROC_FL = 'N' and A.ODS_DLY_PROC_FL ='N' and A.TOT_RWD_PNT <> 0 and A.txn_dtetm > sysdate-366
UNION ALL
select B.CUST_CR_ID TXN_ID, B.CR_DTETM TXN_DTETM, nvl(B.DIV_NO,0), B.CR_STORE_NO STORE_NO, '1' TXN_OFR_CD, B.CUST_RLTNSHP_NO, NVL(B.BUS_PROG_ID,1), B.CR_PNT TOT_RWD_PNT, 0 TOT_RWD_AMT, 0 BNS_PNT, 0 BASE_PNT, B.CR_PNT MAN_CR_PNT, 0 TOT_TXN_AMT, B.PROC_FL, NULL BATCH_RUN_ID, 'B' IND, 'MANUAL' TXN_TYPE_ID, B.CR_UPD_DTETM POINT_ALCTN_DTTM, -- B.CR_DTETM POINT_ALCTN_DTTM, /*Changed b.cr_dtetm to b.cr_upd_dtetm to capture points allocation date*/ NULL CHKOUT_NO, NULL TKT_NBR from $$CTR_SCH.CUST_CR B where ODS_DLY_PROC_FL = 'N' UNION ALL select TXN_ID, TXN_DTETM, DIV_NO, STORE_NO, '1' OFFER_ID, CUST_RLTNSHP_NO, bus_prog_id, sum(tot_rwd_pnt), sum(tot_rwd_amt), sum(bns_pnt) bns_pnt, sum(base_pnt) base_pnt, sum(CR_PNT) CR_PNT, sum(tot_rwd_amt) tot_txn_amt, PROC_FL, batch_run_id, 'C' IND, 'REFUNDS' , POINT_ALCTN_DTTM, CHKOUT_NO, TKT_NBR from ( select E.CUST_RLTNSHP_NO, A.DIV_NO, A.STORE_NO, A.TXN_ID, '1' OFFER_ID, D.rfnd_BASE_PNT base_pnt, D.rfnd_BNS_PNT bns_pnt, D.RFND_Amt tot_rwd_amt, D.tot_rfnd_pnt tot_rwd_pnt , 0 CR_PNT, NVL(( select TXN_DTETM from $$CTR_SCH.bskt_sls_summ where txn_id=A.txn_id and txn_dtetm between to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 00:00:00','dd/mm/yyyy hh24:mi:ss') and to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 23:59:59','dd/mm/yyyy hh24:mi:ss')),A.txn_dtetm
) TXN_DTETM, D.File_no , A.batch_run_id , A.proc_fl, A.bus_prog_id, B.CRE_DTETM POINT_ALCTN_DTTM, ( select CHKOUT_NO from $$CTR_SCH.bskt_sls_summ where txn_id=A.txn_id and txn_dtetm between to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 00:00:00','dd/mm/yyyy hh24:mi:ss') and to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 23:59:59','dd/mm/yyyy hh24:mi:ss')
) CHKOUT_NO, ( select TKT_NBR from $$CTR_SCH.bskt_sls_summ where txn_id=A.txn_id and txn_dtetm between to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 00:00:00','dd/mm/yyyy hh24:mi:ss') and to_date(to_char(A.txn_dtetm,'dd/mm/yyyy')||' 23:59:59','dd/mm/yyyy hh24:mi:ss')
) TKT_NBR from $$CTR_SCH.BSKT_SLS_RWD_SUMM A, $$CTR_SCH.BATCH_RUN_MASTER B, $$CTR_SCH.RFND_CALC_DTL D, $$CTR_SCH.BSKT_SLS_RWD_SUMM E where A.txn_id = D.TXN_ID and D.PRNT_TXN_ID = E.txn_id and A.batch_run_id = B.batch_run_id and B.ODS_DLY_PROC_FL = 'N' and A.ODS_DLY_PROC_FL ='N' and D.tot_rfnd_pnt <> 0 and A.txn_dtetm > sysdate-366 and D.txn_dtetm > sysdate-366 and E.txn_dtetm > sysdate-366 ) group by TXN_ID, TXN_DTETM, DIV_NO, STORE_NO, '1', CUST_RLTNSHP_NO, bus_prog_id, PROC_FL, batch_run_id, POINT_ALCTN_DTTM, CHKOUT_NO, TKT_NBR
